import React,{useState,useMemo} from 'react'
import Short from './Short'

export default function ShortList(props) {
    const [sort, setShort] = useState('')
    const [sort_2, setShort_2] = useState('')
      const shortList = useMemo(()=>{
        if(sort === false)
            return props.date
        return props.date.filter(f => {
            let d = String(f.color)
            return d.includes(sort);
        });
      }, [sort, props.date])

      const shortList_2 = useMemo(()=>{
        if(sort_2 === false)
            return shortList
        return shortList.filter(f => {
            let d = String(f.id)
            return d.includes(sort_2);
        });
      }, [sort_2, shortList])
    return (
        <div>
            <input
                placeholder="Поиск"
                value={sort}
                onChange={e=>setShort(e.target.value)} 
                 />

            <input
                placeholder="Поиск"
                value={sort_2}
                onChange={e=>setShort_2(e.target.value)} 
                 />
            <h1>{<Short/>.map(p => <h3>{p.color}</h3>)}</h1>

        </div>
    )
}
