import React, { useState } from "react";
import {FaStar} from "react-icons/fa";

const Star = ({selected = false , onSelect = f => f}) => (
    <FaStar color={selected ? "green": "red"} onClick={onSelect}/>);
const creatArray = length => [...Array(length)];

function StarRating({style={},totalStarts = 5}) {
    const [selectedStars,setSelectedStars] = useState(0);
    return (
        <div style={{...s}}>
            {creatArray(totalStarts).map((n,i) => (<Star key={i} selected={selectedStars > i} onSelect={()=>setSelectedStars(i+1)}/>))}
            <p>
                {selectedStars} of {totalStarts} stars
            </p>
        </div>
    );
}

export default StarRating;
