import React,{useMemo,useState} from 'react'



export default function Short(props) {
    const [sort, setShort] = useState('')
      const shortList = useMemo(()=>{
        if(sort === false)
            return props.date
        return props.date.filter(f => {
            let d = String(f.color)
            return d.includes(sort);
        });
      }, [sort, props.date])


    return (shortList)
}
