import React, { useState } from "react";
import {FaStar} from "react-icons/fa";

const Star = ({selected = false , onSelect = f => f}) => (
    <FaStar color={selected ? "green": "red"}/>
)
const creatArray = length => [...Array(length)];

function StarRating({totalStarts = 5}) {
    const [selectedStars] = useState(3);
    return (
        <section>
            {creatArray(totalStarts).map((n,i) => (<Star key={i} selected={selectedStars > i}/>))}
            <p>
                {selectedStars} of {totalStarts} stars
            </p>
        </section>
    );
}

export default StarRating;
