        },
        "unit": {
            "id": 1,
            "name": "шт"
        },
        "serial_number": "11",
        "price": 100000.0
    },
    {
        "id": 3,
        "type_techn": {
            "id": 1,
            "name": "АРМ"
        },
        "name_technicians": {
            "id": 2,
            "name": "АРМ 02"
        },
        "unit": {
            "id": 1,
            "name": "шт"
        },
        "serial_number": "123",
        "price": 123123.0
    }
])
const serchedTech = useMemo(()=>{
  if(searcheQuery === false)
    return people
  return people.filter(tech => {
    let price = String(tech.serial_number)
    return price.includes(searcheQuery)});
}, [searcheQuery, people])


  // useEffect(()=>{
