  //   })
  // },[])

  return (
    <div >
      <div>
        <input 
        placeholder='Поиск'
        value={searcheQuery}
        onChange={e=>setSearcheQuery(e.target.value)}
        />
      </div>
     <FormInput date={serchedTech}/>
    </div>
  );
}

export default ListEquipment;
