import React, { useEffect,useState } from 'react';
import axios from 'axios';


function App() {
  const [people, setPeople] = useState([])

  useEffect(()=>{
    axios({
      method: "GET",
      url: "http://127.0.0.1:8000/api/tech"
    }).then(response=>{
      setPeople(response.data)
    })
  },[])

  return (
    <div className="App">
      <table className='table table-striped'>
        {people.map(p=>(
          <tr key={p.id}>
            <th>
              {p.type_techn.name}
            </th>
            <th>
              {p.name_technicians.name}
            </th>
            <th>
              {p.unit.name}
            </th>
            <th>
              {p.serial_number}
            </th>
            <th>
              {p.price}
            </th>
          </tr>
        ))}
        <tr>
          <td></td>
        </tr>
      </table>

      <ul>
        {people.map(p=> (
          <li key={p.id}>{p.type_techn.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
