import React, { useEffect,useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function ListEquipment() {

  const [people, setPeople] = useState([
    {
        "id": 1,
        "type_techn": {
            "id": 1,
            "name": "АРМ"
        },
        "name_technicians": {
            "id": 1,
            "name": "АРМ 01"
        },
        "unit": {
            "id": 1,
            "name": "шт"
        },
        "serial_number": "1010",
        "price": 1000.0
    },
    {
        "id": 2,
        "type_techn": {
            "id": 1,
            "name": "АРМ"
        },
        "name_technicians": {
            "id": 2,
            "name": "АРМ 02"
        },
        "unit": {
            "id": 1,
            "name": "шт"
        },
        "serial_number": "11",
        "price": 100000.0
    },
    {
        "id": 3,
        "type_techn": {
            "id": 1,
            "name": "АРМ"
        },
        "name_technicians": {
            "id": 2,
            "name": "АРМ 02"
        },
        "unit": {
            "id": 1,
            "name": "шт"
        },
        "serial_number": "123",
        "price": 123123.0
    }
])


  // useEffect(()=>{
  //   axios({
  //     method: "GET",
  //     url: "http://127.0.0.1:8000/api/tech"
  //   }).then(response=>{
  //     setPeople(response.data)
  //   })
  // },[])

  return (
    <div >
      <table className='table table-striped'>
        <thead>
          <tr>
            <th>
              #
            </th>
            <th>
              Серійний номер
            </th>
            <th>
              Тип техніки
            </th>
            <th>
              Назва техніки
            </th>
            <th>
              Одиниці виміру
            </th>
            <th>
              Ціна
            </th>
          </tr>
        </thead>
        <tbody>
        {people.map((p,index)=>(
          <tr>
            <th>
              {index+1}
            </th>
            <td>
              <Link to={p.serial_number}>{p.serial_number}</Link>
            </td>
            <td>
              {p.type_techn.name}
            </td>
            <td>
              {p.name_technicians.name}
            </td>
            <td>
              {p.unit.name}
            </td>
            <td>
              {p.price}
            </td>
          </tr>
        ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListEquipment;
