import React, { useEffect,useState } from 'react';
import axios from 'axios';

function DetailedList() {
  const [people, setPeople] = useState([
    {
        "id": 1,
        "id_doc": {
            "id": 1,
            "number_doc": "100",
            "name_doc": "Наряд",
