  //     url: "http://127.0.0.1:8000/api/tech"
  //   }).then(response=>{
  //     setPeople(response.data)
  //   })
  // },[])

  return (

    <div >
      <table className='table table-striped'>
        <thead>
          <tr>
            <th>
              #
            </th>
            <th>
              Підрозділ
            </th>
            <th>
              Категорія
            </th>
            <th>
              Кількість
            </th>
            <th>
              Документ
            </th>
          </tr>
        </thead>
        <tbody>
        {people.map((p,index)=>(
          <tr >
            <th>
              {index+1}
            </th>
            <td>
              {p.subdivision_which.subdivisions}
            </td>
            <td>
              {p.сategory.categorys}
            </td>
            <td>
              {p.remainder}
            </td>
            <td>
              {p.id_doc.name_doc}
            </td>

          </tr>
        ))}
        </tbody>
      </table>
    </div>
  );
}

export default DetailedList;
