import React, { useEffect,useState } from 'react';
import axios from 'axios';


function App() {
  const [people, setPeople] = useState([])

  useEffect(()=>{
    axios({
      method: "GET",
      url: "http://127.0.0.1:8000/api/tech"
    }).then(response=>{
      setPeople(response.data)
    })
  },[])

  return (
    <div className="App">
      <table className='table table-striped'>
        <thead>
          <tr>
            <th>
              #
            </th>
            <th>
              Тип техніки
            </th>
            <th>
              Назва техніки
            </th>
            <th>
              Одиниці виміру
            </th>
            <th>
              Серійний номер
            </th>
            <th>
              Ціна
            </th>
          </tr>
        </thead>
        <tbody>
        {people.map(p=>(
          <tr key={p.id}>
            <th>

            </th>
            <td>
              {p.type_techn.name}
            </td>
            <td>
              {p.name_technicians.name}
            </td>
            <td>
              {p.unit.name}
            </td>
            <td>
              {p.serial_number}
            </td>
            <td>
              {p.price}
            </td>
          </tr>
        ))}
        </tbody>
      </table>

      <ul>
        {people.map(p=> (
          <li key={p.id}>{p.type_techn.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
